        /// <summary>
        /// Configures the HttpClients.
        /// </summary>
        /// <param name="client"></param>
        /// <param name="builder"></param>
        /// <returns></returns>
        public HostConfiguration AddHetznerCloudHttpClients
        (
            Action<HttpClient>? client = null, Action<IHttpClientBuilder>? builder = null)
        {
            if (client == null)
                client = c => c.BaseAddress = new Uri(ClientUtils.BASE_ADDRESS);

            List<IHttpClientBuilder> builders = new List<IHttpClientBuilder>();

            builders.Add(_services.AddHttpClient<IActionsApi, ActionsApi>(client));
            builders.Add(_services.AddHttpClient<ICertificatesApi, CertificatesApi>(client));
            builders.Add(_services.AddHttpClient<IDatacentersApi, DatacentersApi>(client));
            builders.Add(_services.AddHttpClient<IFirewallsApi, FirewallsApi>(client));
            builders.Add(_services.AddHttpClient<IFloatingIpsApi, FloatingIpsApi>(client));
            builders.Add(_services.AddHttpClient<IImagesApi, ImagesApi>(client));
            builders.Add(_services.AddHttpClient<IIsosApi, IsosApi>(client));
            builders.Add(_services.AddHttpClient<ILoadBalancerTypesApi, LoadBalancerTypesApi>(client));
            builders.Add(_services.AddHttpClient<ILoadBalancersApi, LoadBalancersApi>(client));
            builders.Add(_services.AddHttpClient<ILocationsApi, LocationsApi>(client));
            builders.Add(_services.AddHttpClient<INetworksApi, NetworksApi>(client));
            builders.Add(_services.AddHttpClient<IPlacementGroupsApi, PlacementGroupsApi>(client));
            builders.Add(_services.AddHttpClient<IPricingApi, PricingApi>(client));
            builders.Add(_services.AddHttpClient<IPrimaryIpsApi, PrimaryIpsApi>(client));
            builders.Add(_services.AddHttpClient<IServerTypesApi, ServerTypesApi>(client));
            builders.Add(_services.AddHttpClient<IServersApi, ServersApi>(client));
            builders.Add(_services.AddHttpClient<ISshKeysApi, SshKeysApi>(client));
            builders.Add(_services.AddHttpClient<IVolumesApi, VolumesApi>(client));
            
            if (builder != null)
                foreach (IHttpClientBuilder instance in builders)
                    builder(instance);

            HttpClientsAdded = true;

            return this;
        }

        /// <summary>
        /// Configures the JsonSerializerSettings
        /// </summary>
        /// <param name="options"></param>
        /// <returns></returns>
        public HostConfiguration ConfigureJsonOptions(Action<JsonSerializerOptions> options)
        {
            options(_jsonOptions);

            return this;
        }

        /// <summary>
        /// Adds tokens to your IServiceCollection
        /// </summary>
        /// <typeparam name="TTokenBase"></typeparam>
        /// <param name="token"></param>
        /// <returns></returns>
        public HostConfiguration AddTokens<TTokenBase>(TTokenBase token) where TTokenBase : TokenBase
        {
            return AddTokens(new TTokenBase[]{ token });
        }

        /// <summary>
        /// Adds tokens to your IServiceCollection
        /// </summary>
        /// <typeparam name="TTokenBase"></typeparam>
        /// <param name="tokens"></param>
        /// <returns></returns>
        public HostConfiguration AddTokens<TTokenBase>(IEnumerable<TTokenBase> tokens) where TTokenBase : TokenBase
        {
            TokenContainer<TTokenBase> container = new TokenContainer<TTokenBase>(tokens);
            _services.AddSingleton(services => container);

            return this;
        }

        /// <summary>
        /// Adds a token provider to your IServiceCollection
        /// </summary>
        /// <typeparam name="TTokenProvider"></typeparam>
        /// <typeparam name="TTokenBase"></typeparam>
        /// <returns></returns>
        public HostConfiguration UseProvider<TTokenProvider, TTokenBase>() 
            where TTokenProvider : TokenProvider<TTokenBase>
            where TTokenBase : TokenBase
        {
            _services.AddSingleton<TTokenProvider>();
            _services.AddSingleton<TokenProvider<TTokenBase>>(services => services.GetRequiredService<TTokenProvider>());

            return this;
        }
    }
